import React,{ useState } from 'react';
import auth from '../firebase';


const Login = () =>{

const [studentID,setStudentID] = useState(''); //studentID
const [password,setPassword]  = useState(''); //password

const handleLogin = () => {
        console.log('login',studentID,password);
        //auth.signInWithStudentIDAndPassword(studentID,password);
    }
   
    const handleStudentID = Event =>{
       setStudentID(Event.target.value);
    }//studentID
    const handlePassword = Event =>{
        setPassword(Event.target.value);
    }//password


    return(
        <div>
            <header >Please input your passport account name and your Password /กรุณากรอกบัญชี PSU Passport และรหัสผ่าน</header><br></br>
            <input type="studentID" placeholder = "PSU Passport Account Name" size="50" height="10" onChange={handleStudentID}/> 
           <p></p>
            <input type="password" placeholder = "Password" size="50" onChange={handlePassword}/> <br></br><br></br>

            <button type = "button" onClick={handleLogin} >Login </button>
        </div>
    )
}

export default Login;
