import React,{ useState } from 'react';
import auth from '../firebase';
import '../App.css';


const Login = () =>{


const [Username,setUsername] = useState(''); //studentID
const [password,setPassword]  = useState(''); //password

const handleLogin = async () => {
       console.log('login',Username,password);
       const response= await auth.signInWithStudentIDAndPassword(Username,password);

      const { user } = response
      console.log('user',user);
    }
   
    const handleUsername = Event =>{
       setUsername(Event.target.value);
    }//studentID
    const handlePassword = Event =>{
        setPassword(Event.target.value);
    }//password


    return(
        <div > 
            <header class="pleaseinputtext" >Please input your passport account name and your Password /กรุณากรอกบัญชี PSU Passport และรหัสผ่าน</header><br></br>
       
            <input type="studentID" placeholder = "PSU Passport Account Name" class="holder"  onChange={handleUsername}/> 
           <p></p>
            <input type="password"  placeholder = "Password" class="holder"  onChange={handlePassword}/> <br></br><br></br>

            <button type = "button" class="button:hover" class="button" onClick={handleLogin} >Login </button>
        </div>
    )
}

export default Login;
