import React,{ useState } from 'react';
import auth from '../firebase';
import '../App.css';


const Login = () =>{

const [studentID,setStudentID] = useState(''); //studentID
const [password,setPassword]  = useState(''); //password

const handleLogin = () => {
        console.log('login',studentID,password);
        //auth.signInWithStudentIDAndPassword(studentID,password);
    }
   
    const handleStudentID = Event =>{
       setStudentID(Event.target.value);
    }//studentID
    const handlePassword = Event =>{
        setPassword(Event.target.value);
    }//password


    return(<div><img src="psu.jpg" width="500" height="500"></img>
        <div >
            
            <header class="pleaseinputtext" >Please input your passport account name and your Password /กรุณากรอกบัญชี PSU Passport และรหัสผ่าน</header><br></br>
       
            <input type="studentID" placeholder = "PSU Passport Account Name" class="holder"  onChange={handleStudentID}/> 
           <p></p>
            <input type="password"  placeholder = "Password" class="holder"  onChange={handlePassword}/> <br></br><br></br>

            <button type = "button" class="button:hover" class="button" onClick={handleLogin} >Login </button>
        </div>
        </div>
    )
}

export default Login;
